# Stencils or kernels to smooth data arrays


import numba


@numba.stencil
def kernel_1(a, b):
    return 9.0 * a[ 0, 0, 0] * b[ 0, 0, 0] \
               + a[+1, 0, 0] * b[+1, 0, 0] \
               + a[ 0,+1, 0] * b[ 0,+1, 0] \
               + a[ 0, 0,+1] * b[ 0, 0,+1] \
               + a[-1, 0, 0] * b[-1, 0, 0] \
               + a[ 0,-1, 0] * b[ 0,-1, 0] \
               + a[ 0, 0,-1] * b[ 0, 0,-1] \
               + a[+1,+1, 0] * b[+1,+1, 0] \
               + a[ 0,+1,+1] * b[ 0,+1,+1] \
               + a[+1, 0,+1] * b[+1, 0,+1] \
               + a[-1,-1, 0] * b[-1,-1, 0] \
               + a[ 0,-1,-1] * b[ 0,-1,-1] \
               + a[-1, 0,-1] * b[-1, 0,-1] \
               + a[+1,-1, 0] * b[+1,-1, 0] \
               + a[-1,+1, 0] * b[-1,+1, 0] \
               + a[ 0,+1,-1] * b[ 0,+1,-1] \
               + a[ 0,-1,+1] * b[ 0,-1,+1] \
               + a[+1, 0,-1] * b[+1, 0,-1] \
               + a[-1, 0,+1] * b[-1, 0,+1] \
               + a[+1,+1,+1] * b[+1,+1,+1] \
               + a[-1,-1,-1] * b[-1,-1,-1] \
               + a[+1,-1,-1] * b[+1,-1,-1] \
               + a[-1,+1,+1] * b[-1,+1,+1] \
               + a[-1,+1,-1] * b[-1,+1,-1] \
               + a[+1,-1,+1] * b[+1,-1,+1] \
               + a[+1,+1,-1] * b[+1,+1,-1] \
               + a[-1,-1,+1] * b[-1,-1,+1]


@numba.stencil
def kernel_2(b):
    return 9.0 * b[ 0, 0, 0] \
               + b[+1, 0, 0] \
               + b[ 0,+1, 0] \
               + b[ 0, 0,+1] \
               + b[-1, 0, 0] \
               + b[ 0,-1, 0] \
               + b[ 0, 0,-1] \
               + b[+1,+1, 0] \
               + b[ 0,+1,+1] \
               + b[+1, 0,+1] \
               + b[-1,-1, 0] \
               + b[ 0,-1,-1] \
               + b[-1, 0,-1] \
               + b[+1,-1, 0] \
               + b[-1,+1, 0] \
               + b[ 0,+1,-1] \
               + b[ 0,-1,+1] \
               + b[+1, 0,-1] \
               + b[-1, 0,+1] \
               + b[+1,+1,+1] \
               + b[-1,-1,-1] \
               + b[+1,-1,-1] \
               + b[-1,+1,+1] \
               + b[-1,+1,-1] \
               + b[+1,-1,+1] \
               + b[+1,+1,-1] \
               + b[-1,-1,+1]
